DataSource.SQL:
IF @ITSMKey IS NULL
BEGIN
  DECLARE
    @CounterIdent VARCHAR(100) = 'REL-',
    @NextNumber INT

  EXEC @NextNumber = usr.CounterGetNext @CounterIdent, @UserID;

  UPDATE usr.ITSMRelease
  SET ITSMKey = CONCAT(@CounterIdent, RIGHT('000000' + CAST(@NextNumber AS VARCHAR(6)), 6))
  WHERE ID = @ID
END
